import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './TodoItem';
import './TodoList.css';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/todos', {
          headers: { 'x-auth-token': token }
        });
        setTodos(response.data);
      } catch (err) {
        console.error('Failed to fetch todos:', err.response ? err.response.data : err.message);
      }
    };

    fetchTodos();
  }, []);

  const handleAddTodo = async (e) => {
    e.preventDefault();
    if (newTodo.trim()) {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.post('http://localhost:5000/api/todos', { text: newTodo }, {
          headers: { 'x-auth-token': token }
        });
        setTodos([...todos, response.data]);
        setNewTodo('');
      } catch (err) {
        console.error('Failed to add todo:', err.response ? err.response.data : err.message);
      }
    }
  };

  const handleToggleComplete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const todo = todos.find(todo => todo.id === id);
      const response = await axios.put(`http://localhost:5000/api/todos/${id}`, {
        text: todo.text,
        completed: !todo.completed
      }, {
        headers: { 'x-auth-token': token }
      });
      setTodos(todos.map(todo => (todo.id === id ? response.data : todo)));
    } catch (err) {
      console.error('Failed to update todo:', err.response ? err.response.data : err.message);
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/todos/${id}`, {
        headers: { 'x-auth-token': token }
      });
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (err) {
      console.error('Failed to delete todo:', err.response ? err.response.data : err.message);
    }
  };

  return (
    <div className="todo-list-container">
      <h2>Todo List</h2>
      <form onSubmit={handleAddTodo}>
        <input 
          type="text" 
          placeholder="New Todo" 
          value={newTodo} 
          onChange={(e) => setNewTodo(e.target.value)} 
        />
        <button type="submit">Add Todo</button>
      </form>
      <ul>
        {todos.map(todo => (
          <TodoItem 
            key={todo.id} 
            todo={todo} 
            onToggleComplete={handleToggleComplete} 
            onDelete={handleDeleteTodo} 
          />
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
